mapper.select:
SELECT *
FROM orders

    itemName LIKE CONCAT('%', #{itemName}, '%')

        AND BuyerID = #{buyerID}


        AND SellerID = #{sellerID}


        AND OrderStatus = #{status}



ORDER BY CreatedAt DESC
LIMIT #{size} OFFSET #{offset}

SELECT COALESCE(COUNT(*), 0)
FROM orders

    itemName LIKE CONCAT('%', #{itemName}, '%')

        AND BuyerID = #{buyerID}


        AND SellerID = #{sellerID}


        AND OrderStatus = #{status}
